import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const OwnerDetails = () => {
  const { ownerId } = useParams();
  const [owner, setOwner] = useState(null);
  const [pets, setPets] = useState([]);

  useEffect(() => {
    fetch(`/api/owners/${ownerId}`)
      .then((response) => response.json())
      .then((data) => {
        setOwner(data.owner);
        setPets(data.pets);
      })
      .catch((error) => console.error('Error fetching owner details:', error));
  }, [ownerId]);

  const handleEditPet = (petId) => {
    // Logic to edit pet information
    console.log(`Edit pet with ID: ${petId}`);
  };

  return (
    <div>
      {owner && <h1>{owner.name}'s Pets</h1>}
      <ul>
        {pets.map((pet) => (
          <li key={pet.id}>
            {pet.name} - {pet.type}
            <button onClick={() => handleEditPet(pet.id)}>Edit</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OwnerDetails;
