import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function OwnersList() {
  const [owners, setOwners] = useState([]);
  const [error, setError] = useState('');
  const [selectedOwners, setSelectedOwners] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/owners')
      .then(res => setOwners(res.data))
      .catch(() => setError('Error loading owners'));
  }, []);

  const handleCheckboxChange = (ownerId) => {
    setSelectedOwners(prev => {
      if (prev.includes(ownerId)) {
        return prev.filter(id => id !== ownerId);
      } else {
        return [...prev, ownerId];
      }
    });
  };

  const handleDeleteSelected = async () => {
    try {
      await Promise.all(selectedOwners.map(ownerId => 
        axios.delete(`http://localhost:5000/owners/${ownerId}`)
      ));
      setOwners(owners.filter(owner => !selectedOwners.includes(owner._id)));
      setSelectedOwners([]);
    } catch (err) {
      setError('Error deleting owners');
    }
  };

  if (error) return <div className="alert alert-danger">{error}</div>;

  return (
    <div className="owners-card card p-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3>All Owners</h3>
        {selectedOwners.length > 0 && (
          <button 
            className="btn btn-danger btn-sm"
            onClick={handleDeleteSelected}
          >
            Delete Selected ({selectedOwners.length})
          </button>
        )}
      </div>
      {owners.length === 0 ? (
        <p>No owners found.</p>
      ) : (
        <ul className="list-group">
          {owners.map(owner => (
            <li className="list-group-item d-flex justify-content-between align-items-center" key={owner._id}>
              <div className="d-flex align-items-center">
                <input
                  type="checkbox"
                  className="me-3"
                  checked={selectedOwners.includes(owner._id)}
                  onChange={() => handleCheckboxChange(owner._id)}
                />
                <Link to={`/owners/${owner._id}`} className="owner-link">
                  {owner.firstName} {owner.lastName}
                </Link>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default OwnersList;
