import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function OwnerSearchForm() {
  const [lastName, setLastName] = useState('');
  const [results, setResults] = useState([]);
  const [error, setError] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.get(`http://localhost:5000/owners?lastName=${lastName}`);
      setResults(res.data);
    } catch (err) {
      setError('Error searching owners');
    }
  };

  return (
    <div>
      <form className="card p-4 mb-3" onSubmit={handleSubmit}>
        <h3>Search Owners by Last Name</h3>
        <div className="mb-3">
          <input className="form-control" value={lastName} onChange={e => setLastName(e.target.value)} placeholder="Enter last name" required />
        </div>
        <button className="btn btn-primary" type="submit">Search</button>
        <Link to="/owners/add" className="btn btn-success ms-2">Add New Owner</Link>
      </form>
      {error && <div className="alert alert-danger">{error}</div>}
      {results.length > 0 && (
        <div className="card p-3">
          <h5>Results:</h5>
          <ul className="list-group">
            {results.map(owner => (
              <li className="list-group-item" key={owner._id}>
                <Link to={`/owners/${owner._id}`}>{owner.firstName} {owner.lastName}</Link>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default OwnerSearchForm;
